"use strict";(self.webpackChunkblog=self.webpackChunkblog||[]).push([[1477],{10:t=>{t.exports=JSON.parse('{"blogPosts":[{"id":"quote","metadata":{"permalink":"/blog/quote","editUrl":"https://github.com/zhangxuewen/zhangxuewen.github.io/tree/main/blog/2019-05-31-GraduationQuote.md","source":"@site/blog/2019-05-31-GraduationQuote.md","title":"\u6bd5\u4e1a\u8d60\u8a00(\u8f6c\u8f7d)","description":"\u5728\u6bd5\u4e1a\u751f\u4eec\u8fc8\u51fa\u8c61\u7259\u5854\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5e94\u8be5\u542c\u4ece\u54ea\u4e9b\u4eba\u7684\u5efa\u8bae\uff1f\u5728\u8d70\u5411\u72ec\u7acb\u548c\u81ea\u4e3b\u7684\u4f1f\u5927\u5f81\u7a0b\u4e2d\uff0c\u4ed6\u4eec\u8be5\u5438\u53d6\u54ea\u4e9b\u6559\u8bad\uff1f\u542c\u4e00\u542c\u5404\u9886\u57df\u6210\u529f\u8005\u4eec\u7684\u5efa\u8bae\u6216\u8bb8\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba \u662f\u5bcc\u6709\u8fdc\u89c1\u7684\u5546\u4e1a\u9886\u5bfc\u8005\uff0c\u4ed6\u4eec\u4ece\u65e0\u5230\u6709\u5efa\u7acb\u8d77\u81ea\u5df1\u7684\u5e1d\u56fd\uff0c\u5e76\u90fd\u8dfb\u8eab\u4e16\u754c\u6700\u5bcc\u6709\u7684\u4eba\u4e4b\u5217\u3002\u4ee5\u4e0b\u662f\u5386\u5e74\u6765\u591a\u4f4dIT\u9886\u8896\u5728\u6bd5\u4e1a\u5178\u793c\u4e0a\u6240\u505a\u6f14\u8bb2\u7684\u8282\u9009\uff0c\u6211\u4eec\u4ece\u4e2d\u63d0\u53d6\u51fa\u4e86\u53ef\u4f9b\u5e94\u5c4a\u6bd5\u4e1a\u751f\u91c7\u7eb3\u7684\u5efa\u8bae\uff1a","date":"2019-05-31T00:00:00.000Z","formattedDate":"May 31, 2019","tags":[{"label":"\u6210\u957f","permalink":"/blog/tags/\u6210\u957f"}],"readingTime":10,"hasTruncateMarker":false,"authors":[{"name":"zhangxuewen","title":"Full Stack Developer","url":"https://github.com/zhangxuewen","imageURL":"https://avatars.githubusercontent.com/u/1623160?v=4"}],"frontMatter":{"slug":"quote","title":"\u6bd5\u4e1a\u8d60\u8a00(\u8f6c\u8f7d)","author":"zhangxuewen","author_title":"Full Stack Developer","author_url":"https://github.com/zhangxuewen","author_image_url":"https://avatars.githubusercontent.com/u/1623160?v=4","tags":["\u6210\u957f"]},"nextItem":{"title":"\u5f00\u53d1\u7684\u4e09\u5927\u96be","permalink":"/blog/thirdhard"}},"content":"\u5728\u6bd5\u4e1a\u751f\u4eec\u8fc8\u51fa\u8c61\u7259\u5854\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5e94\u8be5\u542c\u4ece\u54ea\u4e9b\u4eba\u7684\u5efa\u8bae\uff1f\u5728\u8d70\u5411\u72ec\u7acb\u548c\u81ea\u4e3b\u7684\u4f1f\u5927\u5f81\u7a0b\u4e2d\uff0c\u4ed6\u4eec\u8be5\u5438\u53d6\u54ea\u4e9b\u6559\u8bad\uff1f\u542c\u4e00\u542c\u5404\u9886\u57df\u6210\u529f\u8005\u4eec\u7684\u5efa\u8bae\u6216\u8bb8\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba \u662f\u5bcc\u6709\u8fdc\u89c1\u7684\u5546\u4e1a\u9886\u5bfc\u8005\uff0c\u4ed6\u4eec\u4ece\u65e0\u5230\u6709\u5efa\u7acb\u8d77\u81ea\u5df1\u7684\u5e1d\u56fd\uff0c\u5e76\u90fd\u8dfb\u8eab\u4e16\u754c\u6700\u5bcc\u6709\u7684\u4eba\u4e4b\u5217\u3002<br />\u4ee5\u4e0b\u662f\u5386\u5e74\u6765\u591a\u4f4dIT\u9886\u8896\u5728\u6bd5\u4e1a\u5178\u793c\u4e0a\u6240\u505a\u6f14\u8bb2\u7684\u8282\u9009\uff0c\u6211\u4eec\u4ece\u4e2d\u63d0\u53d6\u51fa\u4e86\u53ef\u4f9b\u5e94\u5c4a\u6bd5\u4e1a\u751f\u91c7\u7eb3\u7684\u5efa\u8bae\uff1a\\n\\n** \u82f9\u679c\u521b\u59cb\u4eba\u53f2\u8482\u592b\xb7\u4e54\u5e03\u65af\uff08Steve Jobs\uff09\uff1a\u628a\u6bcf\u4e00\u5929\u90fd\u5f53\u6210\u751f\u547d\u4e2d\u7684\u6700\u540e\u4e00\u5929**<br /> \u5728\u621117\u5c81\u7684\u65f6\u5019\uff0c\u6211\u8bfb\u5230\u4e86\u4e00\u53e5\u7bb4\u8a00\uff0c\u5dee\u4e0d\u591a\u662f\u8fd9\u6837\u7684\uff1a\u201c\u5982\u679c\u4f60\u628a\u6bcf\u4e00\u5929\u90fd\u5f53\u4f5c\u751f\u547d\u4e2d\u7684\u6700\u540e\u4e00\u5929\u53bb\u751f\u6d3b\u7684\u8bdd\uff0c\u90a3\u4e48\u7ec8\u6709\u4e00\u5929\u4f60\u4f1a\u53d1\u73b0\u81ea\u5df1\u662f\u6b63\u786e\u7684\u3002\u201d\u8fd9\u53e5\u8bdd \u7ed9\u6211\u7559\u4e0b\u4e86\u6df1\u523b\u7684\u5370\u8c61\uff0c\u4ece\u90a3\u65f6\u7b97\u8d77\u768433\u5e74\u4ee5\u6765\uff0c\u6211\u6bcf\u5929\u65e9\u6668\u90fd\u4f1a\u5bf9\u7740\u955c\u5b50\u95ee\u81ea\u5df1\uff1a\u201c\u5982\u679c\u4eca\u5929\u662f\u6211\u751f\u547d\u4e2d\u7684\u6700\u540e\u4e00\u5929\uff0c\u6211\u8fd8\u4f1a\u505a\u81ea\u5df1\u4eca\u5929\u5373\u5c06\u8981\u505a\u7684\u4e8b\u5417\uff1f\u201d\u5f53 \u7b54\u6848\u8fde\u7eed\u591a\u6b21\u90fd\u662f\u201c\u4e0d\u201d\u65f6\uff0c\u6211\u5c31\u77e5\u9053\u81ea\u5df1\u9700\u8981\u505a\u4e9b\u6539\u53d8\u4e86\u3002\u65af\u5766\u798f\u5927\u5b66\uff0c2005\u5e74\\n\\n** \u5fae\u8f6f\u521b\u59cb\u4eba\u6bd4\u5c14\xb7\u76d6\u8328\uff08Bill Gates\uff09\uff1a\u4f60\u7684\u80fd\u529b\u8d8a\u5927\uff0c\u4eba\u4eec\u5bf9\u4f60\u7684\u671f\u671b\u4e5f\u5c31\u8d8a\u5927**<br /> \u6211\u7684\u6bcd\u4eb2\u5728\u6211\u88ab\u54c8\u4f5b\u5927\u5b66\u5f55\u53d6\u7684\u90a3\u4e00\u5929\u66fe\u7ecf\u611f\u5230\u975e\u5e38\u9a84\u50b2\uff0c\u5979\u4ece\u6ca1\u6709\u505c\u6b62\u7763\u4fc3\u6211\u53bb\u4e3a\u4ed6\u4eba\u505a\u66f4\u591a\u7684\u4e8b\u60c5\u3002\u5728\u6211\u7ed3\u5a5a\u7684\u524d\u51e0\u5929\uff0c\u5979\u4e3b\u6301\u4e86\u4e00\u4e2a\u65b0\u5a18\u8fdb\u6211\u5bb6\u7684\u4eea\u5f0f\u3002\u5728\u8fd9 \u4e2a\u4eea\u5f0f\u4e0a\uff0c\u5979\u9ad8\u58f0\u6717\u8bfb\u4e86\u4e00\u5c01\u5173\u4e8e\u5a5a\u59fb\u7684\u4fe1\uff0c\u8fd9\u662f\u5979\u5199\u7ed9\u6885\u7433\u8fbe\uff08Melinda\uff09\u7684\u3002\u90a3\u65f6\uff0c\u6211\u7684\u6bcd\u4eb2\u5df2\u7ecf\u56e0\u4e3a\u764c\u75c7\u75c5\u5165\u818f\u8093\uff0c\u4f46\u662f\u5979\u8fd8\u662f\u8ba4\u4e3a\u8fd9\u662f\u53c8\u4e00\u4e2a\u4f20\u64ad\u81ea \u5df1\u4fe1\u5ff5\u7684\u673a\u4f1a\u3002\u5728\u90a3\u5c01\u4fe1\u7684\u7ed3\u5c3e\uff0c\u5979\u5199\u9053\uff1a\u201c\u4f60\u7684\u80fd\u529b\u8d8a\u5927\uff0c\u4eba\u4eec\u5bf9\u4f60\u7684\u671f\u671b\u4e5f\u5c31\u8d8a\u5927\u3002\u201d\u54c8\u4f5b\u5927\u5b66\uff0c2007\u5e74\\n\\n **\u6234\u5c14\u521b\u59cb\u4eba\u8fc8\u514b\u5c14\xb7\u6234\u5c14\uff08Michael Dell\uff09\uff1a\u6c38\u8fdc\u4e0d\u8981\u5f53\u623f\u95f4\u91cc\u6700\u806a\u660e\u7684\u90a3\u4e2a\u4eba**<br /> \u6c38\u8fdc\u4e0d\u8981\u5c1d\u8bd5\u6210\u4e3a\u623f\u95f4\u91cc\u6700\u806a\u660e\u7684\u90a3\u4e2a\u4eba\uff0c\u5982\u679c\u4f60\u662f\u7684\u8bdd\uff0c\u6211\u5efa\u8bae\u4f60\u8bf7\u6765\u4e00\u4f4d\u66f4\u806a\u660e\u7684\u2026\u2026\u6216\u8005\u81ea\u5df1\u6362\u4e2a\u4e0d\u540c\u7684\u623f\u95f4\u3002\u5728\u804c\u573a\u4e2d\uff0c\u8fd9\u53eb\u505a\u4eba\u9645\u5173\u7cfb\u3002\u5728\u7ec4\u7ec7\u4e2d\uff0c\u8fd9\u53eb \u505a\u961f\u4f0d\u5efa\u8bbe\u3002\u5728\u751f\u6d3b\u4e2d\uff0c\u8fd9\u88ab\u79f0\u4e3a\u5bb6\u5ead\u3001\u670b\u53cb\u548c\u5708\u5b50\u3002\u6211\u4eec\u90fd\u662f\u5f7c\u6b64\u7684\u793c\u7269\uff0c\u5728\u6211\u6210\u957f\u4e3a\u4e00\u540d\u9886\u5bfc\u8005\u7684\u8fc7\u7a0b\u4e2d\uff0c\u6211\u5f97\u5230\u7684\u542f\u793a\u4e00\u904d\u904d\u5730\u91cd\u590d\uff0c\u5373\u6700\u6709\u76ca\u7684\u7ecf\u9a8c\u662f\u6765\u81ea \u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u3002\u5f97\u514b\u8428\u65af\u5927\u5b66\uff0c2003\u5e74\\n\\n** \u4e9a\u9a6c\u900a\u521b\u59cb\u4eba\u6770\u592b\xb7\u8d1d\u7d22\u65af\uff1a\u5929\u8d4b\u5f97\u6765\u5f88\u5bb9\u6613\uff0c\u800c\u9009\u62e9\u5374\u9887\u4e3a\u8270\u96be**<br /> \u6211\u6597\u80c6\u505a\u4e00\u4e2a\u9884\u6d4b\uff1a\u5728\u4f60\u4eec80\u5c81\u65f6\u67d0\u4e2a\u8ffd\u5fc6\u5f80\u6614\u7684\u65f6\u523b\uff0c\u5f53\u4f60\u4e00\u4e2a\u4eba\u9759\u9759\u5730\u5bf9\u5185\u5fc3\u8bc9\u8bf4\u81ea\u5df1\u7684\u4eba\u751f\u6545\u4e8b\uff0c\u5176\u4e2d\u6700\u4e3a\u5145\u5b9e\u3001\u6700\u6709\u610f\u4e49\u7684\u90a3\u6bb5\u8bb2\u8ff0\u4f1a\u88ab\u4f60\u4eec\u4f5c\u51fa\u7684\u4e00\u7cfb\u5217\u51b3\u5b9a\u6240\u586b\u6ee1\u3002\u6700\u540e\uff0c\u662f\u9009\u62e9\u5851\u9020\u4e86\u6211\u4eec\u7684\u4eba\u751f\u3002\u4e3a\u4f60\u81ea\u5df1\u5851\u9020\u4e00\u4e2a\u4f1f\u5927\u7684\u4eba\u751f\u6545\u4e8b\u5427\u3002\u666e\u6797\u65af\u987f\u5927\u5b66\uff0c2010\u5e74\\n\\n** Facebook\u521b\u59cb\u4eba\u9a6c\u514b\xb7\u624e\u514b\u4f2f\u683c\uff1a\u5982\u679c\u4f60\u559c\u6b22\u81ea\u5df1\u505a\u7684\u4e8b\uff0c\u4e8b\u60c5\u4f1a\u5bb9\u6613\u5f88\u591a**<br /> \u5f53\u4f60\u56de\u5bb6\u5403\u996d\uff0c\u76d8\u5b50\u91cc\u662f\u6700\u96be\u5403\u7684\u852c\u83dc\uff0c\u5982\u679c\u4f60\u613f\u610f\u53ef\u4ee5\u52c9\u5f3a\u81ea\u5df1\u5403\u4e0b\u53bb\u3002\u4f46\u5982\u679c\u4f60\u662f\u73a9\u6e38\u620f\uff0c\u5373\u4f7f\u975e\u5e38\u96be\uff0c\u53ea\u8981\u81ea\u5df1\u559c\u6b22\uff0c\u4f60\u4e5f\u4f1a\u52aa\u529b\u901a\u5173\u3002\u5982\u679c\u4f60\u559c\u6b22\u81ea\u5df1\u505a\u7684\u4e8b\uff0c\u4e8b\u60c5\u4f1a\u5bb9\u6613\u5f88\u591a\uff0c\u800c\u4f60\u4e5f\u5c06\u62e5\u6709\u66f4\u5927\u7684\u51b3\u5fc3\u3002\u52a0\u5dde\u95e8\u7f57\u516c\u56ed\u8d1d\u62c9\u6e2f\u793e\u533a\u5b66\u6821\uff0c2011\u5e74\\n\\n** \u5fae\u8f6fCEO\u53f2\u8482\u592b\xb7\u9c8d\u5c14\u9ed8\uff08Steve Ballmer\uff09\uff1a\u4e0d\u8981\u6fc0\u60c5\uff0c\u8981\u6709\u97e7\u6027**<br /> \u6fc0\u60c5\u662f\u5bf9\u67d0\u4ef6\u4e8b\u7269\u4ea7\u751f\u5174\u594b\u7684\u80fd\u529b\uff0c\u800c\u63a7\u5236\u529b\u548c\u97e7\u6027\u5219\u662f\u4fdd\u6301\u8fd9\u79cd\u5174\u594b\u7684\u80fd\u529b\u3002\u5982\u679c\u4f60\u8003\u5bdf\u4e00\u4e0b\u6211\u4eec\u8fd9\u4e2a\u884c\u4e1a\u7684\u90a3\u4e9b\u521b\u4e1a\u516c\u53f8\uff0c\u4f60\u4f1a\u53d1\u73b0\u5b83\u4eec\u5927\u591a\u6570\u90fd\u5931\u8d25\u4e86\u3002\u5982\u679c \u4f60\u518d\u8003\u5bdf\u4e00\u4e0b\u90a3\u4e9b\u83b7\u5f97\u6210\u529f\u7684\u516c\u53f8\uff0c\u6bd4\u5982\u5fae\u8f6f\u3001\u82f9\u679c\u3001\u8c37\u6b4c\u4ee5\u53caFacebook\uff0c\u51e1\u662f\u4f60\u80fd\u53eb\u51fa\u540d\u5b57\u7684\uff0c\u5b83\u4eec\u90fd\u7ecf\u5386\u8fc7\u56f0\u96be\u65f6\u671f\u3002\u4f60\u53d6\u5f97\u4e86\u4e00\u4e9b\u6210\u529f\uff0c\u4f60\u4e5f\u649e\u4e86\u51e0 \u56de\u5357\u5899\uff0c\u4f60\u4e3a\u65b0\u7684\u70b9\u5b50\u548c\u521b\u65b0\u5c1d\u8bd5\u65b0\u7684\u9014\u5f84\uff0c\u4f46\u672a\u80fd\u6210\u529f\uff0c\u8fd9\u65f6\u5019\u51b3\u5b9a\u4e8b\u4e1a\u6210\u8d25\u7684\u5c31\u662f\uff0c\u4f60\u6709\u591a\u987d\u5f3a\uff0c\u4f60\u6709\u591a\u5927\u7684\u63a7\u5236\u529b\uff0c\u4f60\u6709\u591a\u4e50\u89c2\u4ee5\u53ca\u4f60\u6709\u591a\u5c11\u97e7\u6027\u3002\u5357\u52a0 \u5dde\u5927\u5b66\uff0c2011\u5e74\\n\\n** \u8c37\u6b4c\u521b\u59cb\u4eba\u62c9\u91cc\xb7\u4f69\u5947\uff08Larry Page\uff09\uff1a\u8ffd\u5bfb\u4f1f\u5927\u7684\u68a6\u60f3\uff0c\u4f60\u4e0d\u4f1a\u9047\u5230\u7ade\u4e89**<br /> \u6211\u8ba4\u4e3a\uff0c\u8ffd\u5bfb\u96c4\u5fc3\u4e07\u4e08\u7684\u68a6\u60f3\u901a\u5e38\u66f4\u52a0\u5bb9\u6613\uff0c\u6211\u77e5\u9053\u8fd9\u542c\u8d77\u6765\u5b8c\u5168\u662f\u4e00\u6d3e\u80e1\u8a00\u3002\u4e0d\u8fc7\uff0c\u65e2\u7136\u6ca1\u6709\u522b\u7684\u4eba\u75af\u72c2\u5230\u4f1a\u505a\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5c31\u6ca1\u6709\u7ade\u4e89\u5bf9\u624b\u4e86\u3002\u8fbe\u5230\u8fd9\u79cd\u75af\u72c2\u7a0b \u5ea6\u7684\u4eba\u662f\u5982\u6b64\u4e4b\u5c11\uff0c\u4ee5\u81f3\u4e8e\u6211\u611f\u89c9\u81ea\u5df1\u8ddf\u4ed6\u4eec\u90fd\u8ba4\u8bc6\u3002\u4ed6\u4eec\u50cf\u72d7\u7fa4\u4e00\u6837\u6f2b\u6e38\uff0c\u50cf\u80f6\u6c34\u4e00\u6837\u76f8\u4e92\u4eb2\u8fd1\uff0c\u8fd9\u4e9b\u6700\u4f18\u79c0\u7684\u4eba\u4e50\u4e8e\u63a5\u53d7\u8270\u5de8\u7684\u6311\u6218\uff0c\u8fd9\u5c31\u662f\u5728\u8c37\u6b4c\u53d1\u751f\u7684\u4e8b \u60c5\u3002\u5bc6\u6b47\u6839\u5927\u5b66\uff0c2009\u5e74\\n\\n **\u8c37\u6b4c\u8463\u4e8b\u957f\u57c3\u91cc\u514b\xb7\u65bd\u5bc6\u7279\uff08Eric Schmidt\uff09\uff1a\u4e0d\u8981\u523b\u610f\u5236\u5b9a\u8ba1\u5212**<br /> \u4e0d\u8981\u523b\u610f\u5236\u5b9a\u8ba1\u5212\uff0c\u90a3\u4e9b\u6709\u5173\u8ba1\u5212\u7684\u4e1c\u897f\uff0c\u4f60\u4eec\u53ef\u4ee5\u6254\u6389\u4e86\u3002\u5728\u6211\u770b\u6765\uff0c\u4e00\u5207\u90fd\u5173\u4e4e\u673a\u9047\u548c\u4e89\u53d6\u5230\u81ea\u5df1\u7684\u8fd0\u6c14\u3002\u5982\u679c\u4f60\u89c2\u5bdf\u90a3\u4e9b\u6700\u6210\u529f\u7684\u4eba\u58eb\uff0c\u4f60\u4f1a\u53d1\u73b0\uff0c\u4ed6\u4eec\u5de5\u4f5c \u52aa\u529b\u5e76\u5145\u5206\u5229\u7528\u4e86\u673a\u9047\uff0c\u4f46\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u90a3\u4e9b\u673a\u9047\u4f1a\u964d\u4e34\u5230\u81ea\u5df1\u5934\u4e0a\u3002\u4f60\u65e0\u6cd5\u4e3a\u521b\u65b0\u5236\u5b9a\u8ba1\u5212\uff0c\u4f60\u4e5f\u65e0\u6cd5\u4e3a\u53d1\u660e\u5236\u5b9a\u8ba1\u5212\u3002\u4f60\u6240\u80fd\u505a\u7684\u5c31\u662f\uff0c\u5c3d\u81ea\u5df1\u6700\u5927\u7684\u52aa\u529b\u5446\u5728 \u6b63\u786e\u7684\u5730\u65b9\uff0c\u5e76\u968f\u65f6\u51c6\u5907\u7740\u3002\u4e3e\u4f8b\u6765\u8bf4\uff0c\u6211\u4eec\u90fd\u77e5\u9053\uff0c\u5fc3\u810f\u8d77\u640f\u5668\u5728\u517670\u5e74\u5386\u53f2\u4e2d\u7ecf\u5386\u4e86\u5404\u79cd\u5404\u6837\u7684\u5f62\u5f0f\u624d\u6700\u7ec8\u6295\u5165\u5b9e\u7528\u3002\u5b83\u88ab\u690d\u5165\u5230\u4e00\u4e2a\u53ef\u601c\u4eba\u7684\u4f53\u5185\uff0c\u5728\u66f4\u6362 \u4e8625\u53f0\u8d77\u640f\u5668\u540e\uff0c\u4ed6\u4ecd\u7136\u6d3b\u7740\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u6545\u4e8b\u7684\u91cd\u70b9\u5728\u4e8e\uff0c\u5982\u679c\u5fc3\u810f\u8d77\u640f\u5668\u6ca1\u6709\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u8fd9\u4e2a\u4eba\u5c31\u4e0d\u4f1a\u6709\u5b58\u6d3b\u7684\u673a\u4f1a\uff0c\u8fd9\u4e00\u5207\u4f60\u6c38\u8fdc\u90fd\u641e\u4e0d\u61c2\u3002\u5361\u5185\u57fa -\u6885\u9686\u5927\u5b66\uff0c2009\u5e74\\n\\n** \u6469\u6258\u7f57\u62c9\u80a1\u4e1c\u5361\u5c14\xb7\u4f0a\u574e\uff08Carl Icahn\uff09\uff1a\u72ec\u7acb\u601d\u8003**<br /> \u5f53\u4f60\u4eec\u8e0f\u5165\u793e\u4f1a\uff0c\u4f60\u4eec\u8981\u6210\u4e3a\u4e24\u79cd\u4eba\u4e2d\u7684\u4e00\u79cd\uff0c\u4f60\u4eec\u8981\u6210\u4e3a\u81ea\u5df1\u72ec\u7acb\u601d\u8003\u7684\u4eba\uff0c\u6211\u8ba4\u4e3a\u8fd9\u4e2a\u4e16\u754c\u548c\u6211\u4eec\u7684\u516c\u53f8\u6b63\u5728\u4e3a\u6b64\u7b49\u5f85\u3002\u4e00\u4e9b\u516c\u53f8\u7ba1\u7406\u8005\u771f\u7684\u80fd\u591f\u505a\u5230\u8fd9\u4e00\u70b9\uff0c \u4ed6\u4eec\u5bcc\u6709\u521b\u65b0\u6027\uff0c\u80fd\u591f\u53cd\u6f6e\u6d41\u800c\u52a8\u2026\u2026\u4f60\u4eec\u5e94\u8be5\u8bd5\u7740\u5bf9\u6297\u6f6e\u6d41\uff0c\u5373\u4f7f\u8fd9\u53ef\u80fd\u4f1a\u8ba9\u4f60\u4eec\u5931\u53bb\u5de5\u4f5c\u6216\u8005\u664b\u5347\u7684\u673a\u4f1a\u3002\u4f46\u6700\u7ec8\uff0c\u4f60\u8fd8\u662f\u8981\u72ec\u7acb\u601d\u8003\uff0c\u52c7\u4e8e\u521b\u65b0\u3002\u5982\u679c\u4f60\u6709\u4e86 \u60f3\u6cd5\uff0c\u90a3\u5c31\u53bb\u62cd\u684c\u5b50\u5427\uff0c\u4e0d\u8981\u62c5\u5fc3\uff0c\u56e0\u4e3a\u8fd9\u6b63\u662f\u516c\u53f8\u6240\u9700\u8981\u7684\u3002\u5fb7\u96f7\u514b\u585e\u5c14\u5927\u5b66\u5546\u5b66\u9662\uff0c2008\u5e74\\n\\n** \u96c5\u864e\u521b\u59cb\u4eba\u6768\u81f4\u8fdc\uff1a\u4e0d\u8981\u88ab\u7ecf\u6d4e\u4f4e\u8ff7\u7684\u65b0\u95fb\u5413\u574f**<br /> \u6211\u80fd\u5411\u4f60\u4eec\u4fdd\u8bc1\uff0c\u7ecf\u6d4e\u4f4e\u8ff7\u6b63\u662f\u5b55\u80b2\u4f1f\u5927\u4e8b\u7269\u7684\u65f6\u5019\uff0c\u5c31\u50cf\u73b0\u5728\u8fd9\u6837\u3002\u96c5\u864e\u521b\u7acb\u4e8e\u4e0a\u4e16\u7eaa90\u5e74\u4ee3\u521d\u7684\u7ecf\u6d4e\u8870\u9000\u65f6\u671f\uff0c\u5176\u4ed6\u4f1f\u5927\u7684\u516c\u53f8\u3001\u60f3\u6cd5\u548c\u4ea7\u54c1\uff0c\u751a\u81f3\u662f\u793e\u4f1a\u8fd0 \u52a8\uff0c\u90fd\u662f\u5728\u4eba\u4eec\u629b\u5f03\u6559\u6761\u548c\u91c7\u7528\u65b0\u65b9\u6cd5\u7684\u65f6\u5019\u53d1\u5c55\u6210\u5f62\u7684\u3002\u4ece\u67d0\u4e9b\u65b9\u9762\u770b\uff0c\u5728\u8fd9\u79cd\u7ecf\u6d4e\u6062\u590d\u65f6\u671f\u6bd5\u4e1a\u662f\u518d\u597d\u4e0d\u8fc7\u7684\u65f6\u673a\u3002\u590f\u5a01\u5937\u5927\u5b66\uff0c2009\u5e74\u6765\u6e90\u798f\u5e03\u65af\u4e2d\u6587\u7f51)"},{"id":"thirdhard","metadata":{"permalink":"/blog/thirdhard","editUrl":"https://github.com/zhangxuewen/zhangxuewen.github.io/tree/main/blog/2019-05-30-thirdhard.md","source":"@site/blog/2019-05-30-thirdhard.md","title":"\u5f00\u53d1\u7684\u4e09\u5927\u96be","description":"\u7a0b\u5e8f\u5458\u4e00\u822c\u9047\u5230\u8fd9\u4e09\u79cd\u9879\u76ee\u90fd\u4f1a\u8111\u888b\u75db\uff0c\u6539\u9020\uff0c\u91cd\u6784\uff0c\u8fc1\u79fb\u3002\u56e0\u4e3a\u5b83\u4eec\u90fd\u5c5e\u4e8e\u6539\u52a8\u5927\uff0c\u53c8\u5f88\u5bb9\u6613\u51fa\u95ee\u9898\u7684\u9879\u76ee\uff0c\u4f46\u662f\u5bf9\u4e1a\u52a1\u7684\u4ef7\u503c\u53c8\u5f88\u9610\u8ff0\uff08\u4e3b\u8981\u662f\u9488\u5bf9\u4e1a\u52a1\u9a71\u52a8\u578b\u7684\u516c\u53f8\uff0c\u5bf9\u4e8e\u6280\u672f\u9a71\u52a8\u578b\u7684\u516c\u53f8\u6bd4\u8f83\u597d\u89e3\u91ca\uff09\u3002\u4e0b\u9762\u6211\u8be6\u7ec6\u7ed9\u5927\u5bb6\u89e3\u91ca\u4e00\u4e0b\u8fd9\u4e09\u79cd\u6280\u672f\u96be\u70b9","date":"2019-05-30T00:00:00.000Z","formattedDate":"May 30, 2019","tags":[{"label":"\u67b6\u6784","permalink":"/blog/tags/\u67b6\u6784"}],"readingTime":2.015,"hasTruncateMarker":false,"authors":[{"name":"zhangxuewen","title":"Full Stack Developer","url":"https://github.com/zhangxuewen","imageURL":"https://avatars.githubusercontent.com/u/1623160?v=4"}],"frontMatter":{"slug":"thirdhard","title":"\u5f00\u53d1\u7684\u4e09\u5927\u96be","author":"zhangxuewen","author_title":"Full Stack Developer","author_url":"https://github.com/zhangxuewen","author_image_url":"https://avatars.githubusercontent.com/u/1623160?v=4","tags":["\u67b6\u6784"]},"prevItem":{"title":"\u6bd5\u4e1a\u8d60\u8a00(\u8f6c\u8f7d)","permalink":"/blog/quote"},"nextItem":{"title":"\u65b0\u7684\u8d77\u70b9\uff0c\u535a\u5ba2\u91cd\u5f00\u5f20","permalink":"/blog/start"}},"content":"\u7a0b\u5e8f\u5458\u4e00\u822c\u9047\u5230\u8fd9\u4e09\u79cd\u9879\u76ee\u90fd\u4f1a\u8111\u888b\u75db\uff0c\u6539\u9020\uff0c\u91cd\u6784\uff0c\u8fc1\u79fb\u3002\u56e0\u4e3a\u5b83\u4eec\u90fd\u5c5e\u4e8e\u6539\u52a8\u5927\uff0c\u53c8\u5f88\u5bb9\u6613\u51fa\u95ee\u9898\u7684\u9879\u76ee\uff0c\u4f46\u662f\u5bf9\u4e1a\u52a1\u7684\u4ef7\u503c\u53c8\u5f88\u9610\u8ff0\uff08\u4e3b\u8981\u662f\u9488\u5bf9\u4e1a\u52a1\u9a71\u52a8\u578b\u7684\u516c\u53f8\uff0c\u5bf9\u4e8e\u6280\u672f\u9a71\u52a8\u578b\u7684\u516c\u53f8\u6bd4\u8f83\u597d\u89e3\u91ca\uff09\u3002\u4e0b\u9762\u6211\u8be6\u7ec6\u7ed9\u5927\u5bb6\u89e3\u91ca\u4e00\u4e0b\u8fd9\u4e09\u79cd\u6280\u672f\u96be\u70b9\\n\\n### \u6539\u9020\\n\u9996\u5148\u6765\u8bf4\u6539\u9020\uff0c\u5b83\u7684\u52a8\u4f5c\u4e00\u822c\u5c0f\u4e8e\u91cd\u6784\uff0c\u5c31\u50cf\u4e00\u6761\u8def\uff0c\u53ef\u4ee5\u901a\u8fc7\u5c0f\u7684\u6539\u9020\u3002\u8ba9\u8def\u66f4\u597d\u8d70\u3002\\n\u6539\u9020\u60f3\u5230\u7684\u662f\u4e1a\u52a1\u529f\u80fd\u7684\u6539\u9020 \uff0c\u9700\u6c42\u4e0d\u4e71\u79ef\u5230\u670d\u52a1\u4e0a\uff0c\u9700\u8981\u91cd\u65b0\u68b3\u7406 \u8fdb\u884c\u6539\u9020\uff0c\\n\\n\\n\\n### \u91cd\u6784\\n\u5728\u9a6c\u4e01\xb7\u798f\u52d2\u7684\u300a\u91cd\u6784\uff1a\u6539\u5584\u65e2\u6709\u4ee3\u7801\u7684\u8bbe\u8ba1\u300b\u4e2d\uff0c\u6240\u8c13\u91cd\u6784\uff08refactoring\uff09\u662f\u8fd9\u6837\u4e00\u4e2a\u8fc7\u7a0b\uff1a\u5728\u4e0d\u6539\u53d8\u4ee3\u7801\u5916\u5728\u884c\u4e3a\u7684\u524d\u63d0\u4e0b\uff0c\u5bf9\u4ee3\u7801\u505a\u51fa\u4fee\u6539\uff0c\u4ee5\u6539\u8fdb\u7a0b\u5e8f\u7684\u5185\u90e8\u7ed3\u6784\u3002\u91cd\u6784\u662f\u4e00\u79cd\u7ecf\u5343\\n\u5fae\u670d\u52a1\u91cd\u6784 \\n\u8bed\u8a00\u91cd\u6784\\n\u8bbe\u8ba1\u6a21\u5f0f\u91cd\u6784\\n\u91cd\u6784\u662f\u66f4\u5927\u7684\u624b\u672f\uff0c\u5728\u4eac\u4e1c  \u8fdb\u884csoa\u7684\u91cd\u6784 \uff0c\u5728\u652f\u4ed8\u5b9d\u8fdb\u884c\u53d1\u73b0\u5e73\u53f0\u7684\u91cd\u6784 \uff0c\u5728\u9605\u6587\u8fdb\u884c\u4f1a\u5458\u7684\u91cd\u6784\u7b49 \\n\\n### \u8fc1\u79fb\\n\u5f53\u8def\u5df2\u7ecf\u7834\u5230\u4e0d\u80fd\u6539\u9020\u7684\u65f6\u5019\uff0c\u5c31\u53ea\u80fd\u91cd\u4fee\u4e86 \uff0c\u8fc1\u79fb\u9762\u4e34\u7684\u662f\u4e0d\u5f97\u4e0d\uff0c\u56e0\u4e3a\u8def\u524d\u9762\u4e0d\u80fd\u8d70\u4e86\uff0c\u9700\u8981\u8fc1\u79fb\u5230\u53e6\u4e00\u6761\u8def\u4e0a\uff0c\u4f46\u662f\u4f9d\u8d56\u8981\u5230\u540c\u4e00\u76ee\u6807\u3002\\n\u8fc1\u79fb\u4e00\u822c\u662f\u539f\u6765\u7684\u4e1c\u897f\u4e0d\u80fd\u7528\u4e86\uff1a\u6570\u636e\u5e93\u4e0a\u4e91\u8fc1\u79fb   \uff0c\u4ecetars\u5e73\u53f0\u8fc1\u79fb\u5230trps\u5e73\u53f0\\n\u6570\u636e\u5e93\u8fc1\u79fb \\n\u8d44\u4ea7\u8fc1\u79fb\\n\u670d\u52a1\u8fc1\u79fb\\n\\nhttps://gcp.infoq.cn/article.html?id=30c9ff79-c7ca-45c8-83c7-091a1f8b9922\\nhttps://www.infoq.cn/article/cebfobzp21aqv4eaydeq\\nhttps://github.com/phodal/migration"},{"id":"start","metadata":{"permalink":"/blog/start","editUrl":"https://github.com/zhangxuewen/zhangxuewen.github.io/tree/main/blog/2019-01-30-start.md","source":"@site/blog/2019-01-30-start.md","title":"\u65b0\u7684\u8d77\u70b9\uff0c\u535a\u5ba2\u91cd\u5f00\u5f20","description":"\u4f5c\u4e3a\u4e00\u4e2a\u521a\u8fc735\u5c81\u7684\u7a0b\u5e8f\u5458\uff0c\u56de\u9996\u8fc7\u53bb\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u5728\u4e92\u8054\u7f51\u5e76\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\uff0c\u8fd9\u5e76\u4e0d\u662f\u81ea\u5df1\u7684\u521d\u5fe0\u3002\u603b\u7ed3\u8fc7\u53bb\u5c31\u662f\u81ea\u5df1\u7684\u60f0\u6027\u592a\u5f3a\u4e86\u3002\u5176\u5b9e\u5728\u5927\u5b66\u7684\u65f6\u5019\u81ea\u5df1\u5c31\u5df2\u7ecf\u642d\u5efa\u4e86\u81ea\u5df1\u7684\u72ec\u7acb\u6280\u672f\u535a\u5ba2\uff0c\u4f46\u662f\u65ad\u65ad\u7eed\u7eed\u5199\u5199\u505c\u505c\uff0c\u535a\u5ba2\u6280\u672f\u7528\u4e86\u5f88\u591aWordPress\uff0cJekyll\u3001Hugo\u3001Hexo\uff0c\u4f46\u662f\u5185\u5bb9\u6c89\u6dc0\u786e\u5f88\u5c11\u3002\u4e0b\u4e00\u4e2a\u5341\u5e74\u5df2\u7ecf\u5f00\u59cb\uff0c\u5e0c\u671b\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u514b\u670d\u61d2\u60f0\uff0c\u6301\u7eed\u7684\u8f93\u5165\u548c\u8f93\u51fa\uff0c\u6784\u5efa\u81ea\u5df1\u7684\u672a\u6765\u7684\u4eba\u751f\u4f53\u7cfb\u3002","date":"2019-01-30T00:00:00.000Z","formattedDate":"January 30, 2019","tags":[{"label":"\u6210\u957f","permalink":"/blog/tags/\u6210\u957f"}],"readingTime":4.54,"hasTruncateMarker":false,"authors":[{"name":"zhangxuewen","title":"Full Stack Developer","url":"https://github.com/zhangxuewen","imageURL":"https://avatars.githubusercontent.com/u/1623160?v=4"}],"frontMatter":{"slug":"start","title":"\u65b0\u7684\u8d77\u70b9\uff0c\u535a\u5ba2\u91cd\u5f00\u5f20","author":"zhangxuewen","author_title":"Full Stack Developer","author_url":"https://github.com/zhangxuewen","author_image_url":"https://avatars.githubusercontent.com/u/1623160?v=4","tags":["\u6210\u957f"]},"prevItem":{"title":"\u5f00\u53d1\u7684\u4e09\u5927\u96be","permalink":"/blog/thirdhard"}},"content":"\u4f5c\u4e3a\u4e00\u4e2a\u521a\u8fc735\u5c81\u7684\u7a0b\u5e8f\u5458\uff0c\u56de\u9996\u8fc7\u53bb\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u5728\u4e92\u8054\u7f51\u5e76\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\uff0c\u8fd9\u5e76\u4e0d\u662f\u81ea\u5df1\u7684\u521d\u5fe0\u3002\u603b\u7ed3\u8fc7\u53bb\u5c31\u662f\u81ea\u5df1\u7684\u60f0\u6027\u592a\u5f3a\u4e86\u3002\u5176\u5b9e\u5728\u5927\u5b66\u7684\u65f6\u5019\u81ea\u5df1\u5c31\u5df2\u7ecf\u642d\u5efa\u4e86\u81ea\u5df1\u7684\u72ec\u7acb\u6280\u672f\u535a\u5ba2\uff0c\u4f46\u662f\u65ad\u65ad\u7eed\u7eed\u5199\u5199\u505c\u505c\uff0c\u535a\u5ba2\u6280\u672f\u7528\u4e86\u5f88\u591aWordPress\uff0cJekyll\u3001Hugo\u3001Hexo\uff0c\u4f46\u662f\u5185\u5bb9\u6c89\u6dc0\u786e\u5f88\u5c11\u3002\u4e0b\u4e00\u4e2a\u5341\u5e74\u5df2\u7ecf\u5f00\u59cb\uff0c\u5e0c\u671b\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u514b\u670d\u61d2\u60f0\uff0c\u6301\u7eed\u7684\u8f93\u5165\u548c\u8f93\u51fa\uff0c\u6784\u5efa\u81ea\u5df1\u7684\u672a\u6765\u7684\u4eba\u751f\u4f53\u7cfb\u3002\\n### \u4e3a\u4ec0\u4e48\u8981\u5199\u535a\u5ba2 \\n\u6211\u8ba4\u4e3a\u5bf9\u4e8e\u4e00\u4e2a\u6280\u672f\u4eba\u5458\uff0c\u642d\u5efa\u4e00\u4e2a\u6280\u672f\u535a\u5ba2\uff0c\u662f\u6211\u4eec\u7684\u957f\u671f\u6709\u4ef7\u503c\u7684\u4e8b \uff0c\u6280\u672f\uff0c\u603b\u7ed3 \uff0c\u590d\u76d8\u603b\u7ed3\\n\u8bb2\u5230\u4e867\u4e2a\u7406\u7531\uff0c\u6211\u89c9\u5f97\u8bf4\u5f97\u5f88\u597d\u3002\\n\\n1. \u5b66\u4f1a\u5199\u4f5cBlog\u7684\u6280\u5de7\uff08teach you the skills of blogging\uff09\\n\\n\u6ca1\u6709\u4eba\u5929\u751f\u4f1a\u5199Blog\uff0c\u6211\u521a\u5f00\u59cb\u7684\u65f6\u5019\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u5199\u3002\u4f46\u662f\uff0c\u7ecf\u8fc7\u4e0d\u65ad\u7684\u5c1d\u8bd5\uff0c\u73b0\u5728\u6211\u77e5\u9053\u600e\u4e48\u53ef\u4ee5\u5199\u51fa\u53d7\u6b22\u8fce\u7684\u6587\u7ae0\u3002\\n\\n2. \u719f\u6089Blog\u5de5\u5177\uff08familiarize you with the tools of blogging\uff09\\n\\n\u5199\u4f5cBlog\uff0c\u53ef\u4ee5\u9009\u62e9\u81ea\u5df1\u642d\u5efa\u5e73\u53f0\uff0c\u4e5f\u53ef\u4ee5\u9009\u62e9\u7f51\u4e0a\u7684\u514d\u8d39Blog\u63d0\u4f9b\u5546\u3002\u6211\u66fe\u7ecf\u8bd5\u7528\u8fc7\u4e0d\u5c11Blog\u8f6f\u4ef6\uff0c\u6700\u540e\u624d\u9009\u62e9\u4e86\u73b0\u5728\u7684Moveable Type\uff0c\u8fd9\u672c\u8eab\u4e5f\u662f\u4e00\u4e2a\u5b66\u4e60\u8fc7\u7a0b\u3002\\n\\n3. \u4fbf\u4e8e\u66f4\u597d\u5730\u5b89\u6392\u65f6\u95f4\uff08help you work out how much time you have\uff09\\n\\n\u5199\u4f5cBlog\u82b1\u8d39\u7684\u65f6\u95f4\uff0c\u8981\u6bd4\u5927\u5bb6\u60f3\u8c61\u7684\u591a\uff0c\u751a\u81f3\u4e5f\u6bd4\u6211\u81ea\u5df1\u60f3\u8c61\u7684\u591a\u3002\u4f46\u662f\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u6bcf\u5929\u6211\u4eec\u53c8\u6709\u5f88\u591a\u65f6\u95f4\u88ab\u65e0\u8c13\u5730\u6d6a\u8d39\u4e86\u3002\u575a\u6301\u5199\u4f5cBlog\u7684\u8fc7\u7a0b\uff0c\u4e5f\u662f\u8fdb\u884c\u66f4\u597d\u7684\u65f6\u95f4\u5b89\u6392\u7684\u8fc7\u7a0b\u3002\\n\\n4. \u4fbf\u4e8e\u4f60\u4e86\u89e3\u81ea\u5df1\u662f\u5426\u53ef\u4ee5\u957f\u671f\u505a\u4e00\u4ef6\u559c\u6b22\u7684\u4e8b\u60c5\uff08help you work out if you can sustain blogging for the long term\uff09\\n\\n\u5f88\u591a\u4eba\u90fd\u6709\u81ea\u5df1\u7684\u7231\u597d\uff0c\u4f46\u662f\u53ea\u6709\u5f53\u4f60\u4eab\u53d7\u5230\u8fd9\u79cd\u7231\u597d\u65f6\uff0c\u4f60\u624d\u4f1a\u957f\u671f\u575a\u6301\u4e0b\u53bb\u3002\u5199\u4f5cBlog\u53ef\u4ee5\u5e2e\u52a9\u4f60\u4f53\u9a8c\u5230\u8fd9\u79cd\u611f\u89c9\u3002\\n\\n5. \u4fbf\u4e8e\u4f53\u9a8cBlog\u6587\u5316\uff08give you a taste of blogging \'culture\'\uff09\\n\\nBlog\u7684\u4e16\u754c\u6709\u4e00\u79cd\u65e0\u5f62\u7684\u793c\u4eea\u3001\u98ce\u683c\u548c\u7528\u8bed\u3002\u719f\u6089\u5b83\u4eec\uff0c\u4f1a\u4f7f\u4f60\u66f4\u597d\u5730\u8868\u8fbe\u81ea\u5df1\u548c\u7406\u89e3\u4ed6\u4eba\u3002\\n\\n6. \u4fbf\u4e8e\u4f60\u5f62\u6210\u548c\u4e86\u89e3\u81ea\u6211\uff08help you define a niche\uff09\\n\\n\u957f\u671f\u5199\u4f5cBlog\u6700\u5927\u7684\u597d\u5904\u4e4b\u4e00\u5c31\u662f\uff0c\u5199\u7740\u5199\u7740\uff0c\u4f60\u7684\u81ea\u6211\u4f1a\u53d8\u5f97\u8d8a\u6765\u8d8a\u6e05\u6670\u3002\u4f60\u6700\u7ec8\u4f1a\u660e\u767d\u81ea\u5df1\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4ee5\u53ca\u81ea\u5df1\u70ed\u7231\u7684\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\\n\\n7. \u5e2e\u52a9\u4f60\u627e\u5230\u8bfb\u8005\uff08help you find a readership\uff09\\n\\n\u4e0e\u4ed6\u4eba\u4ea4\u6d41\u662f\u751f\u547d\u6700\u5927\u7684\u4e50\u8da3\u4e4b\u4e00\u3002\u5199\u4f5cBlog\u53ef\u4ee5\u5e2e\u52a9\u6211\u4eec\u66f4\u597d\u5730\u505a\u5230\u8fd9\u4e00\u70b9\u3002\\n\\n\\n\u5982\u679c\u4f60\u89c9\u5f97\u4f60\u60f3\u8bf4\u7684\u4e1c\u897f\u4e0d\u9002\u5b9c\u8ba9\u4ed6\u4eba\u77e5\u9053\uff0c\u4f60\u53ef\u4ee5\u5728\u81ea\u5df1\u7684\u7535\u8111\u91cc\u5199\uff0c\u4e0d\u7528\u653e\u5230\u7f51\u4e0a\u3002\u8fd9\u6837\u9664\u4e86\u4e0a\u9762\u7b2c7\u70b9\u4ee5\u5916\uff0c\u5176\u4ed66\u70b9\u7684\u597d\u5904\u4e5f\u8fd8\u662f\u9002\u7528\u7684\u3002\\n\\n\u603b\u4e4b\uff0c\u6b63\u662f\u56e0\u4e3a\u4ee5\u4e0a7\u4e2a\u7406\u7531\uff0c\u6240\u4ee5\u6211\u5f3a\u70c8\u5efa\u8bae\uff0c\u6bcf\u4e00\u4e2a\u670b\u53cb\u90fd\u5e94\u8be5\u6709\u4e00\u4e2a\u81ea\u5df1\u7684Blog\uff0c\u5c1d\u8bd5\u5c06\u81ea\u5df1\u7684\u751f\u6d3b\u548c\u60f3\u6cd5\u8bb0\u5f55\u4e0b\u6765\uff0c\u7559\u4e0b\u4e00\u4e9b\u5370\u8bb0\u3002\\n\\n### \u5982\u4f55\u575a\u6301\u5199\u535a\u5ba2\\n\\n\u6709\u8ba1\u5212\u5730\u5199\u4f46\u4e5f\u522b\u592a\u809d\u4e86\uff0c\u5197\u4f59\u89c4\u5212\uff0c\u9002\u5f53\u6c34\u6587\\n\u7ec6\u8282\u578b\u7684\u6587\u7ae0\u4e00\u8e74\u800c\u5c31\u6700\u597d\uff0c\u8d8a\u62d6\u8d8a\u5bb9\u6613\u5c31\u7b97\u4e86\\n\u603b\u7ed3\u7c7b\u5e72\u8d27\u5efa\u8bae\u8fed\u4ee3\u518d\u4f18\u5316\uff0c\u5206\u597d\u51e0\u5468\u5199\u5b8c\u4e5f\u53ef\uff08\u5efa\u8bae\u5148\u8349\u7a3f\uff0c\u5b8c\u6210\u540e\u518d\u53d1\u5e03\uff09\\n\\n\u6700\u540e\u518d\u6b21\u7ed9\u81ea\u5df1\u7acb\u4e2aflag\u5427\uff0c\u572845\u5c81\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u7ed9\u81ea\u5df1\u4e00\u4e2a\u5360\u8d5e\u3002"}]}')}}]);